import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Para las solicitudes HTTP

const MapComponent = () => {
  const [personas, setPersonas] = useState([]);
  const [selectedPersonas, setSelectedPersonas] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Obtener las personas del backend
    axios.get('http://localhost:9001/Laboratorio1/persona', { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } })
      .then(response => setPersonas(response.data))
      .catch(error => console.log('Error fetching personas', error));
  }, []);

  useEffect(() => {
    // Inicializar el mapa
    const initMap = () => {
      const map = new window.google.maps.Map(document.getElementById('map'), {
        center: { lat: 4.43889, lng: -75.23222 }, // Ubicación predeterminada en Ibagué, Tolima
        zoom: 10,
      });

      // Agregar marcadores para las personas seleccionadas
      selectedPersonas.forEach(persona => {
        const marker = new window.google.maps.Marker({
          position: { lat: persona.lat, lng: persona.lng },
          map,
          title: persona.nombre,
        });
      });
    };

    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=API_KEY&callback=initMap`;
    script.defer = true;
    window.initMap = initMap;
    document.head.appendChild(script);

    return () => document.head.removeChild(script);
  }, [selectedPersonas]);

  const handleSelectChange = (e) => {
    const selectedIds = Array.from(e.target.selectedOptions, option => option.value);
    axios.get(`http://localhost:9001/Laboratorio1/coordenadas/${selectedIds}`, { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } })
      .then(response => setSelectedPersonas(response.data))
      .catch(error => console.log('Error fetching coordinates', error));
  };

  return (
    <div className="container-fluid h-100 d-flex flex-column">
      <div className="row flex-grow-1">
        <div id="map" className="col-12 h-100"></div>
      </div>

      <div className="form-group">
        <label htmlFor="personasSelect">Seleccione personas para ver sus ubicaciones:</label>
        <select id="personasSelect" className="form-control" multiple onChange={handleSelectChange}>
          {personas.map(persona => (
            <option key={persona.id} value={persona.id}>
              {persona.nombre}
            </option>
          ))}
        </select>
      </div>

      <button className="btn btn-primary mt-3" onClick={() => navigate('/home')}>Volver a Home</button>
    </div>
  );
};

export default MapComponent;
