import React, { useEffect, useState } from 'react';
import { GoogleMap, Marker } from '@react-google-maps/api';
import Modal from 'react-bootstrap/Modal';
import 'bootstrap/dist/css/bootstrap.min.css';

const MapComponent = () => {
    const [personas, setPersonas] = useState([]);
    const [selectedPersona, setSelectedPersona] = useState(null);
    const [showModal, setShowModal] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            const response = await fetch('http://localhost:9001/Laboratorio1/personas');
            const data = await response.json();
            setPersonas(data);
        };

        fetchData();
    }, []);

    const handleMarkerClick = (persona) => {
        setSelectedPersona(persona);
        setShowModal(true);
    };

    const handleClose = () => setShowModal(false);

    return (
        <div>
            <GoogleMap
                // Aquí debes añadir tus props para el mapa
                center={{ lat: 4.570868, lng: -74.297333 }} // Centrar en Colombia
                zoom={5}
                mapContainerStyle={{ width: '100%', height: '400px' }}
            >
                {personas.map((persona) => (
                    <Marker
                        key={persona.idpersona}
                        position={{
                            lat: persona.coordenadas.latitud,
                            lng: persona.coordenadas.longitud
                        }}
                        onClick={() => handleMarkerClick(persona)}
                    />
                ))}
            </GoogleMap>

            <Modal show={showModal} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Información de la Persona</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {selectedPersona && (
                        <div>
                            <h5>{selectedPersona.primerNombre} {selectedPersona.segundoNombre} {selectedPersona.primerApellido} {selectedPersona.segundoApellido}</h5>
                            <p>Identificación: {selectedPersona.identificacion}</p>
                            <p>Email: {selectedPersona.email}</p>
                            <p>Ubicación: {selectedPersona.ubicacion.direccionCompleta}</p>
                            <p>Edad Clínica: {selectedPersona.edadClinica}</p>
                        </div>
                    )}
                </Modal.Body>
                <Modal.Footer>
                    <button className="btn btn-secondary" onClick={handleClose}>
                        Cerrar
                    </button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default MapComponent;
