const AuthService = {
    authenticate: async (username, password) => {
        try {
            // Primero autentica al usuario
            const authResponse = await axios.post(`${API_URL}/authenticate`, {
                username,
                password,
            });

            if (authResponse.data && authResponse.data.token) {
                // Obtiene la API Key usando el nombre de usuario
                const apiKeyResponse = await axios.get(`${API_URL}/apiKey`, {
                    headers: {
                        username: username, // Envía el username en el header
                    },
                });

                const apiKey = apiKeyResponse.data.apiKey;

                // Ahora realiza otra solicitud que requiere el apikey
                const secureResponse = await axios.get(`${API_URL}/someSecureEndpoint`, {
                    headers: {
                        apikey: apiKey, // Incluye el apikey en los encabezados
                    },
                });

                return {
                    token: authResponse.data.token,
                    apikey: apiKey, // Guarda la API Key de la respuesta
                    secureData: secureResponse.data, // Si necesitas datos seguros
                };
            } else {
                throw new Error('No se pudo autenticar al usuario');
            }
        } catch (error) {
            console.error('Error en la autenticación', error);
            throw error; // Propaga el error para manejarlo en el componente
        }
    },
};
