import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [apiKey, setApiKey] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Primero, obtener la API Key del backend
            const apiKeyResponse = await axios.get(`http://localhost:9001/apiKey`, {
                headers: { username: username }
            });
            const fetchedApiKey = apiKeyResponse.data.apikey;

            // Luego, realizar la autenticación
            const response = await axios.post('http://localhost:9001/authenticate', {
                username,
                password
            }, {
                headers: { apikey: fetchedApiKey }
            });

            // Manejar el token recibido
            console.log(response.data.token);
            // Guardar el token en el localStorage o en el estado
            localStorage.setItem('token', response.data.token);
            // Redirigir o realizar alguna acción tras el inicio de sesión exitoso
        } catch (err) {
            console.error(err);
            setError('Error de autenticación. Verifique sus credenciales.');
        }
    };

    return (
        <div className="container mt-5">
            <h2 className="text-center">Iniciar Sesión</h2>
            {error && <div className="alert alert-danger">{error}</div>}
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="username" className="form-label">Usuario</label>
                    <input
                        type="text"
                        className="form-control"
                        id="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="password" className="form-label">Contraseña</label>
                    <input
                        type="password"
                        className="form-control"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit" className="btn btn-primary">Iniciar Sesión</button>
            </form>
        </div>
    );
};

export default Login;
