import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2'; // Asegúrate de que SweetAlert2 esté instalado

const Login = () => {
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();

        // Validar entradas
        if (!login || !password) {
            Swal.fire('Error', 'Por favor, ingresa el login y la contraseña', 'error');
            return;
        }

        try {
            // Enviar solicitud al backend para validar usuario
            const response = await axios.post('http://localhost:9001/Laboratorio1/personass', {
                login,
                password,
            });

            if (response.status === 200) {
                // Usuario autenticado
                Swal.fire('Éxito', 'Usuario autenticado', 'success');
                
                // Obtener la API Key
                const apiKeyResponse = await axios.get(`http://localhost:9001/apiKey`, {
                    headers: {
                        'Authorization': `Bearer eyJhbGciOiJIUzUxMiJ9.eyJqdGkiOiJQUE9PSUlfSldUIiwic3ViIjoibGl6ZXRoY2FybyIsImF1dGhvcml0aWVzIjpbIlJPTEVfVVNFUiJdLCJpYXQiOjE3Mjg3MDk4NjYsImV4cCI6MTcyOTU3Mzg2Nn0.eldgvkN0MnjzuK7PSktA7tk9oqdE--3N3PB08M4-AzWYTFFLMjAqUADZdJkrdXvHcwjVTH2IKyNfnuDE3oIYqw`, // Asegúrate de que el token esté en la respuesta
                    }
                });
                
                // Guarda la API Key en el estado o contexto según tu lógica
                const apiKey = apiKeyResponse.data.apiKey; // Asegúrate de que esto coincide con tu respuesta
                console.log('API Key:', apiKey);

                // Redireccionar o realizar otras acciones según sea necesario
                navigate('/dashboard'); // Cambia a la ruta correspondiente
            }
        } catch (error) {
            console.error('Error al iniciar sesión:', error);
            Swal.fire('Error', 'Credenciales inválidas o error en la red', 'error');
        }
    };

    return (
        <div className="container">
            <h2>Iniciar Sesión</h2>
            <form onSubmit={handleLogin}>
                <div className="mb-3">
                    <label htmlFor="login" className="form-label">Login</label>
                    <input
                        type="text"
                        className="form-control"
                        id="login"
                        value={login}
                        onChange={(e) => setLogin(e.target.value)}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="password" className="form-label">Contraseña</label>
                    <input
                        type="password"
                        className="form-control"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit" className="btn btn-primary">Iniciar Sesión</button>
            </form>
        </div>
    );
};

export default Login;
