import React, { useEffect, useState, useCallback, useMemo } from 'react';
import { GoogleMap, useLoadScript, MarkerF, InfoWindowF } from '@react-google-maps/api';
import CoordenadasService from '../services/CoordenadasService';

const mapContainerStyle = {
  height: '400px',
  width: '100%',
};

const libraries = ['places'];

function MapaMultiple() {
  const [markers, setMarkers] = useState([]);
  const [selectedMarker, setSelectedMarker] = useState(null);
  const [isInfoWindowOpen, setIsInfoWindowOpen] = useState(false);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [defaultCenter, setDefaultCenter] = useState({ lat: 4.570868, lng: -74.297333 }); // Centro predeterminado

  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: "AIzaSyBomnY9UemevV5IVEXtYHP6SWw3kRsERGc", // Asegúrate de mantener esta clave privada
    libraries,
  });

  const fetchCoordenadas = useCallback(async () => {
    try {
      setIsLoading(true);
      const coordenadasList = await CoordenadasService.getCoordenadas();
      
      if (!coordenadasList || !Array.isArray(coordenadasList)) {
        throw new Error('Se recibieron datos inválidos o incompletos');
      }

      // Mapeo y validación de las coordenadas
      const validMarkers = coordenadasList.map(coordenadas => {
        const lat = parseFloat(coordenadas.latitud);
        const lng = parseFloat(coordenadas.longitud);

        if (isNaN(lat) || isNaN(lng) || lat < -90 || lat > 90 || lng < -180 || lng > 180) {
          throw new Error('Coordenadas fuera de rango válido');
        }

        return {
          id: coordenadas.id,
          marca: coordenadas.marca,
          lat,
          lng,
        };
      });

      setMarkers(validMarkers);
      setError(null);
    } catch (error) {
      console.error('Error al obtener coordenadas:', error);
      setError(error.message || 'Error al obtener las coordenadas');
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchCoordenadas();
  }, [fetchCoordenadas]);

  const handleMarkerClick = useCallback((marker) => {
    setSelectedMarker(marker);
    setIsInfoWindowOpen(true);
  }, []);

  const handleInfoWindowClose = useCallback(() => {
    setIsInfoWindowOpen(false);
    setSelectedMarker(null);
  }, []);

  const handleLocationChange = (event) => {
    const selectedId = event.target.value;
    const location = markers.find(marker => marker.id === selectedId);
    
    // Actualiza el centro del mapa al seleccionar una ubicación
    if (location) {
      setDefaultCenter({ lat: location.lat, lng: location.lng });
      setSelectedLocation(location);
    } else {
      setDefaultCenter({ lat: 4.570868, lng: -74.297333 }); // Centro predeterminado si no se encuentra la ubicación
      setSelectedLocation(null);
    }
  };

  const mapOptions = useMemo(() => ({
    disableDefaultUI: true,
    clickableIcons: false,
    scrollwheel: true,
  }), []);

  const renderMap = useMemo(() => {
    if (!isLoaded) return null;

    return (
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        center={defaultCenter}
        zoom={5} // Ajusta el zoom según lo que necesites
        options={mapOptions}
      >
        {markers.map(marker => (
          <MarkerF
            key={marker.id}
            position={{ lat: marker.lat, lng: marker.lng }}
            onClick={() => handleMarkerClick(marker)}
          >
            {isInfoWindowOpen && selectedMarker?.id === marker.id && (
              <InfoWindowF
                position={{ lat: marker.lat, lng: marker.lng }}
                onCloseClick={handleInfoWindowClose}
              >
                <div>
                  <h3>{marker.marca}</h3>
                  <p>ID: {marker.id}</p>
                  <p>Latitud: {marker.lat.toFixed(6)}</p>
                  <p>Longitud: {marker.lng.toFixed(6)}</p>
                </div>
              </InfoWindowF>
            )}
          </MarkerF>
        ))}
      </GoogleMap>
    );
  }, [isLoaded, markers, isInfoWindowOpen, selectedMarker, handleMarkerClick, handleInfoWindowClose, mapOptions, defaultCenter]);

  if (loadError) {
    return <div>Error al cargar el mapa</div>;
  }

  return (
    <div className="container d-flex flex-column align-items-center justify-content-center min-vh-100 bg-primary">
      <div className="card shadow-lg p-4 w-100" style={{ maxWidth: '800px' }}>
        <h1 className="card-title text-center">Mapa Múltiple</h1>

        {error && (
          <div className="alert alert-danger" role="alert">
            <strong>Error!</strong> {error}
          </div>
        )}

        {/* Lista desplegable para seleccionar ubicación */}
        <div className="mb-3">
          <label htmlFor="locationSelect" className="form-label">Selecciona una Ubicación</label>
          <select
            id="locationSelect"
            className="form-select"
            onChange={handleLocationChange}
          >
            <option value="">--Selecciona una ubicación--</option>
            {markers.map(marker => (
              <option key={marker.id} value={marker.id}>
                {marker.marca}
              </option>
            ))}
          </select>
        </div>

        {isLoading ? (
          <div className="d-flex justify-content-center align-items-center" style={{ height: '400px' }}>
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Cargando...</span>
            </div>
          </div>
        ) : (
          <div className="d-flex justify-content-center">
            {renderMap}
          </div>
        )}
      </div>
    </div>
  );
}

export default MapaMultiple;
