import React, { useEffect, useRef } from 'react';
import { Loader } from '@googlemaps/js-api-loader';

const MapComponent = ({ coordinates }) => {
  const mapRef = useRef(null);
  const loader = new Loader({
    apiKey: 'AIzaSyBtmSbtAkZMgopeMNzXW4NC4LyzXRLr-ck',
    version: 'weekly',
  });

  useEffect(() => {
    loader.load().then(() => {
      const map = new window.google.maps.Map(mapRef.current, {
        center: { lat: -4.4387, lng: -75.2066 }, // Centrar el mapa en Tolima
        zoom: 8,
      });

      const newMarkers = coordinates.map(coord => {
        const lat = parseFloat(coord.latitud);
        const lng = parseFloat(coord.longitud);

        if (isNaN(lat) || isNaN(lng)) {
          console.error('Coordenadas inválidas:', coord);
          return null;
        }

        // Crear contenido del marcador
        const markerContent = document.createElement('div');
        markerContent.style.background = 'white';
        markerContent.style.padding = '5px';
        markerContent.style.borderRadius = '5px';
        markerContent.textContent = coord.nombre || 'Desconocido'; // Usar nombre o "Desconocido"

        // Asegúrate de que AdvancedMarkerElement esté definido
        if (!window.google.maps.marker.AdvancedMarkerElement) {
          console.error('AdvancedMarkerElement no está definido');
          return null;
        }

        // Crear un nuevo marcador utilizando AdvancedMarkerElement
        const marker = new window.google.maps.marker.AdvancedMarkerElement({
          position: { lat, lng },
          map: map,
          title: coord.nombre || 'Desconocido',
          content: markerContent, // Usar el nodo DOM aquí
        });

        return marker;
      }).filter(marker => marker !== null);

      // Ejemplo de cómo añadir event listeners
      document.addEventListener('touchstart', handleTouchStart, { passive: true });
      document.addEventListener('touchmove', handleTouchMove, { passive: true });
    });
  }, [coordinates]);

  const handleTouchStart = (e) => {
    // Manejo de evento touchstart
  };

  const handleTouchMove = (e) => {
    // Manejo de evento touchmove
  };

  return <div ref={mapRef} style={{ height: '400px', width: '100%' }} />;
};

export default MapComponent;
