// Register.jsx

import React, { useState } from 'react';
import PersonaService from '../services/PersonaService';

const Register = () => {
  const [personData, setPersonData] = useState({
    login: PersonaService.getDefaultUser().login,
    idpersona: PersonaService.getDefaultUser().idpersona,
    password: PersonaService.getDefaultUser().password,
    apikey: PersonaService.getDefaultUser().apikey,
  });

  const [message, setMessage] = useState('');

  const handleChange = (event) => {
    const { name, value } = event.target;
    setPersonData({
      ...personData,
      [name]: value,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Registra al usuario usando los datos
      await PersonaService.registerUser(personData);

      // Después del registro, intenta autenticar al usuario
      const authResponse = await authenticateUser(personData.login, personData.password, personData.apikey);

      // Almacena el token en el localStorage
      localStorage.setItem('token', authResponse.token);
      console.log('Usuario registrado y autenticado exitosamente');
    } catch (error) {
      console.error('Error durante el registro o autenticación:', error.message);
      setMessage(error.message); // Muestra el mensaje de error
    }
  };

  const authenticateUser = async (username, password, apikey) => {
    const response = await fetch('http://localhost:9001/Laboratorio1/authenticate', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'apikey': apikey,
      },
      body: JSON.stringify({
        username,
        password,
      }),
    });

    if (!response.ok) {
      const errorText = await response.text();
      throw new Error(`Error de autenticación: ${errorText}`);
    }

    return response.json();
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Registro de Usuario</h2>
      {message && <p style={{ color: 'red' }}>{message}</p>}
      <div>
        <label>Login:</label>
        <input type="text" name="login" value={personData.login} readOnly />
      </div>
      <div>
        <label>ID Persona:</label>
        <input type="text" name="idpersona" value={personData.idpersona} readOnly />
      </div>
      <div>
        <label>Contraseña:</label>
        <input type="password" name="password" value={personData.password} onChange={handleChange} />
      </div>
      <div>
        <label>API Key:</label>
        <input type="text" name="apikey" value={personData.apikey} readOnly />
      </div>
      <button type="submit">Registrar</button>
    </form>
  );
};

export default Register;
