import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import "bootstrap/dist/css/bootstrap.min.css";
import PersonaService from "../services/PersonaService";

const Home = () => {
  const [personas, setPersonas] = useState([]); // Estado para almacenar las personas
  const [error, setError] = useState(""); // Estado para manejar errores
  const [isHovered, setIsHovered] = useState({}); // Estado para manejar el hover de botones
  const navigate = useNavigate();

  // Efecto para verificar el token al cargar el componente
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login"); // Redirigir a login si no hay token
    } else {
      loadPersonas(); // Cargar las personas
    }
  }, []);

  // Función para cargar las personas desde el servicio
  const loadPersonas = async () => {
    try {
      const data = await PersonaService.getAllPersons(); // Llama al servicio para obtener las personas
      console.log("Datos cargados:", data); // Verifica los datos cargados
      setPersonas(data); // Guarda los datos en el estado
    } catch (error) {
      console.error("Error loading personas:", error);
      setError("Error al cargar las personas."); // Muestra el error en la interfaz
    }
  };

  // Función para eliminar una persona
  const handleDelete = async (id) => {
    const result = await Swal.fire({
      title: "¿Estás seguro?",
      text: "¡No podrás recuperar este registro!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "¡Sí, eliminar!",
    });

    if (result.isConfirmed) {
      try {
        await PersonaService.deletePerson(id); // Llama al servicio para eliminar la persona
        loadPersonas(); // Recarga la lista de personas
        Swal.fire("Eliminado", "El registro ha sido eliminado.", "success");
      } catch (error) {
        console.error("Error al eliminar la persona:", error);
        Swal.fire("Error", "No se pudo eliminar el registro.", "error");
      }
    }
  };

  // Función para cerrar sesión
  const handleLogout = () => {
    Swal.fire({
      title: "¿Estás seguro que deseas cerrar sesión?",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "Sí, cerrar sesión",
      cancelButtonText: "Cancelar",
    }).then((result) => {
      if (result.isConfirmed) {
        localStorage.removeItem("token"); // Elimina el token del localStorage
        navigate("/login"); // Redirige al login
      }
    });
  };

  return (
    <div
      className="container-fluid"
      style={{
        background: "linear-gradient(to right, #c1d3ff, #c9b1ff)",
        minHeight: "100vh",
        padding: "20px",
      }}
    >
      <h1 className="mb-4">Bienvenido a la aplicación</h1>
      <button
        className="btn btn-warning mb-3"
        onClick={handleLogout}
        style={{
          transition: "background-color 0.3s",
          backgroundColor: isHovered ? "#138496" : "#b81762",
        }}
      >
        Cerrar Sesión
      </button>
      {error && <div className="alert alert-danger">{error}</div>}{" "}
      {/* Mostrar error si existe */}
      <table className="table table-striped">
        <thead>
          <tr>
            <th>ID</th>
            <th>Primer Nombre</th>
            <th>Segundo Nombre</th>
            <th>Primer Apellido</th>
            <th>Segundo Apellido</th>
            <th>Edad</th>
            <th>Identificación</th>
            <th>Email</th>
            <th>Fecha de Nacimiento</th>
            <th>Edad Clínica</th>
            <th>Ubicación</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {personas.length > 0 ? (
            personas.map((persona) => (
              <tr key={persona.idpersona}>
                <td>{persona.idpersona || "No disponible"}</td>
                <td>{persona.primerNombre || "No disponible"}</td>
                <td>{persona.segundoNombre || "No disponible"}</td>
                <td>{persona.primerApellido || "No disponible"}</td>
                <td>{persona.segundoApellido || "No disponible"}</td>
                <td>{persona.edad || "No disponible"}</td>
                <td>{persona.identificacion || "No disponible"}</td>
                <td>{persona.email || "No disponible"}</td>
                <td>{persona.fechaNacimiento || "No disponible"}</td>
                <td>{persona.edadClinica || "No disponible"}</td>
                <td>
                  {persona.ubicacion
                    ? `${persona.ubicacion.lugar}, ${persona.ubicacion.ciudad}, ${persona.ubicacion.departamento}`
                    : "No disponible"}
                </td>
                <td>
                  <Link
                    to={`/update/${persona.idpersona}`}
                    className="btn btn-success me-2"
                    style={{
                      transition: "background-color 0.3s",
                      backgroundColor: isHovered[persona.idpersona + "-update"]
                        ? "#28a745"
                        : "#878080",
                    }}
                    onMouseEnter={() =>
                      setIsHovered((prev) => ({
                        ...prev,
                        [persona.idpersona + "-update"]: true,
                      }))
                    }
                    onMouseLeave={() =>
                      setIsHovered((prev) => ({
                        ...prev,
                        [persona.idpersona + "-update"]: false,
                      }))
                    }
                  >
                    Actualizar
                  </Link>
                  <button
                    className="btn btn-danger"
                    onClick={() => handleDelete(persona.idpersona)}
                    style={{
                      transition: "background-color 0.3s",
                      backgroundColor: isHovered[persona.idpersona + "-delete"]
                        ? "#a9a6a6f5"
                        : "#fb0101",
                    }}
                    onMouseEnter={() =>
                      setIsHovered((prev) => ({
                        ...prev,
                        [persona.idpersona + "-delete"]: true,
                      }))
                    }
                    onMouseLeave={() =>
                      setIsHovered((prev) => ({
                        ...prev,
                        [persona.idpersona + "-delete"]: false,
                      }))
                    }
                  >
                    Eliminar
                  </button>
                  <Link
                    to={`/coordenadas/${persona.idpersona}`}
                    className="btn btn-success me-2"
                    style={{
                      transition: "background-color 0.3s",
                      backgroundColor: isHovered[persona.id + "-localitation"]
                        ? "#28a745"
                        : "#878080",
                    }}
                    onMouseEnter={() =>
                      setIsHovered((prev) => ({
                        ...prev,
                        [persona.idpersona + "-update"]: true,
                      }))
                    }
                    onMouseLeave={() =>
                      setIsHovered((prev) => ({
                        ...prev,
                        [persona.idpersona + "-update"]: false,
                      }))
                    }
                  >
                    Ver Ubicación
                  </Link>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="12" className="text-center">
                No hay personas disponibles.
              </td>
            </tr>
          )}
        </tbody>
      </table>
      {/* Centrar el botón de "VER TODAS LAS UBICACIONES" */}
      <div className="d-flex justify-content-center mt-4">
        <Link
          to="/mapa"
          className="btn btn-info"
          onMouseEnter={() => setIsHovered((prev) => ({ ...prev, map: true }))}
          onMouseLeave={() => setIsHovered((prev) => ({ ...prev, map: false }))}
          style={{
            transition: "background-color 0.3s",
            backgroundColor: isHovered.map ? "#138496" : "#b81762",
          }}
        >
          Ver Ubicaciones en Mapa
        </Link>
      </div>
    </div>
  );
};

export default Home;
