import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // Importar useHistory
import 'bootstrap/dist/css/bootstrap.min.css';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [apiKey, setApiKey] = useState('');
    const [error, setError] = useState('');
    const history = useHistory(); // Hook para la redirección

    useEffect(() => {
        const fetchApiKey = async () => {
            try {
                const response = await axios.get(`http://localhost:9001/apiKey`, {
                    headers: {
                        username: username // Suponiendo que la API requiere el username
                    }
                });
                setApiKey(response.data.apiKey); // Asigna la API Key recuperada
            } catch (err) {
                console.error('Error al obtener la API Key:', err);
            }
        };

        if (username) {
            fetchApiKey();
        }
    }, [username]); // Ejecuta cada vez que el username cambie

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('http://localhost:9001/authenticate', {
                username,
                password
            }, {
                headers: {
                    apikey: apiKey // Envía el apiKey aquí
                }
            });
            console.log(response.data);
            // Redirigir a la página principal después de un inicio de sesión exitoso
            history.push('/home'); // Cambia '/home' por la ruta que desees
        } catch (error) {
            setError('Credenciales inválidas');
            console.error('Error al autenticar:', error);
        }
    };

    return (
        <div className="container mt-5">
            <div className="card" style={{ maxWidth: '400px', margin: 'auto' }}>
                <div className="card-header">
                    <h2 className="text-center">Iniciar Sesión</h2>
                </div>
                <div className="card-body">
                    <form onSubmit={handleSubmit}>
                        {error && <div className="alert alert-danger">{error}</div>}
                        <div className="form-group">
                            <label htmlFor="username">Nombre de usuario</label>
                            <input
                                type="text"
                                className="form-control"
                                id="username"
                                placeholder="Ingresa tu nombre de usuario"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                required
                            />
                        </div>
                        <div className="form-group mt-3">
                            <label htmlFor="password">Contraseña</label>
                            <input
                                type="password"
                                className="form-control"
                                id="password"
                                placeholder="Ingresa tu contraseña"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </div>
                        <div className="form-group mt-3">
                            <label htmlFor="apikey">API Key</label>
                            <input
                                type="text"
                                className="form-control"
                                id="apikey"
                                placeholder="API Key"
                                value={apiKey}
                                readOnly // Hace que el campo sea de solo lectura
                            />
                        </div>
                        <button type="submit" className="btn btn-primary mt-3">Iniciar Sesión</button>
                        <button 
                            type="button" 
                            className="btn btn-link mt-3" 
                            onClick={() => history.push('/register')} // Redirige a la página de registro
                        >
                            Registrarse
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Login;
