import React, { useEffect, useState, useCallback, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import PersonaService from '../services/PersonaService';
import { useAuth } from '../context/AuthContext';
import { Loader } from '@googlemaps/js-api-loader';

const MapComponent = () => {
    const [personas, setPersonas] = useState([]);
    const [selectedPersonas, setSelectedPersonas] = useState([]);
    const [map, setMap] = useState(null);
    const [markers, setMarkers] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const navigate = useNavigate();
    const mapRef = useRef(null);
  
    const { authData } = useAuth();
    const { username, password } = authData;

    useEffect(() => {
        PersonaService.getAllPersons()
            .then(data => {
                if (data) {
                    setPersonas(data);
                }
            })
            .catch(error => console.error('Error al obtener personas', error));
    }, []);

    const initMap = useCallback(async () => {
        if (!map && mapRef.current) {
            const loader = new Loader({
                apiKey: 'AIzaSyBtmSbtAkZMgopeMNzXW4NC4LyzXRLr-ck', // Reemplaza con tu API Key
                version: 'weekly',
                libraries: ['places', 'marker'] // Asegúrate de incluir 'marker'
            });

            try {
                const google = await loader.load();
                const newMap = new google.maps.Map(mapRef.current, {
                    center: { lat: 4.43889, lng: -75.23222 },
                    zoom: 10,
                });
                setMap(newMap);
            } catch (error) {
                console.error('Error loading Google Maps:', error);
            }
        }
    }, [map]);

    useEffect(() => {
        initMap();
    }, [initMap]);

    const updateMarkers = useCallback(() => {
        if (map && selectedPersonas.length > 0) {
            // Limpiar marcadores existentes
            markers.forEach(marker => marker.setMap(null));
            setMarkers([]);

            PersonaService.fetchCoordinates(selectedPersonas.map(p => p.id), { username, password })
                .then(coordinates => {
                    const newMarkers = coordinates.map(coord => {
                        const lat = parseFloat(coord.latitud);
                        const lng = parseFloat(coord.longitud);
                        
                        if (isNaN(lat) || isNaN(lng)) {
                            console.error('Coordenadas inválidas:', coord);
                            return null;
                        }

                        // Crear un nuevo marcador utilizando AdvancedMarkerElement
                        const marker = new window.google.maps.marker.AdvancedMarkerElement({
                            position: { lat, lng },
                            map: map,
                            title: coord.nombre || 'Desconocido',
                            content: `<div style="background: white; padding: 5px; border-radius: 5px;">${coord.nombre || 'Desconocido'}</div>`, // Puedes personalizar el contenido
                        });

                        return marker;
                    }).filter(marker => marker !== null);

                    setMarkers(newMarkers);

                    if (newMarkers.length > 0) {
                        const bounds = new window.google.maps.LatLngBounds();
                        newMarkers.forEach(marker => bounds.extend(marker.getPosition()));
                        map.fitBounds(bounds);
                    } else {
                        console.warn('No se encontraron coordenadas válidas');
                    }
                })
                .catch(error => console.error('Error al obtener coordenadas', error));
        }
    }, [map, selectedPersonas, username, password]);

    useEffect(() => {
        if (map) {
            updateMarkers();
        }
    }, [map, updateMarkers]);

    const handleSelectChange = (e) => {
        const selectedIds = Array.from(e.target.selectedOptions, option => option.value);
        const selectedPersons = personas.filter(persona => selectedIds.includes(persona.id.toString()));
        setSelectedPersonas(selectedPersons);
    };

    const handleSelectAll = () => {
        setSelectedPersonas(personas);
    };

    const handleSearch = (e) => {
        setSearchTerm(e.target.value);
    };

    const filteredPersonas = personas.filter(persona => 
        `${persona.primerNombre} ${persona.segundoNombre} ${persona.primerApellido} ${persona.segundoApellido}`
            .toLowerCase()
            .includes(searchTerm.toLowerCase())
    );

    return (
        <div className="container mt-4" style={{ background: 'linear-gradient(to right, #0066ff, #00ccff)', padding: '20px', borderRadius: '8px' }}>
            <h2 className="mb-4 text-white">Mapa de Ubicaciones</h2>
            <div className="row">
                <div className="col-md-8">
                    <div ref={mapRef} style={{ height: '600px', width: '100%' }}></div>
                </div>
                <div className="col-md-4">
                    <div className="form-group mb-3">
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Buscar por nombre completo"
                            value={searchTerm}
                            onChange={handleSearch}
                        />
                    </div>
                    <div className="form-group mb-3">
                        <label htmlFor="personasSelect" className="text-white">Seleccione personas:</label>
                        <select 
                            id="personasSelect" 
                            className="form-control" 
                            multiple 
                            onChange={handleSelectChange}
                            size="10"
                            value={selectedPersonas.map(p => p.id.toString())}
                        >
                            {filteredPersonas.map(persona => (
                                <option key={persona.id} value={persona.id}>
                                    {`${persona.primerNombre} ${persona.segundoNombre} ${persona.primerApellido} ${persona.segundoApellido}`}
                                </option>
                            ))}
                        </select>
                    </div>
                    <button className="btn btn-secondary mb-3 me-2" style={{ backgroundColor: '#4caf50', borderColor: '#4caf50' }} onClick={handleSelectAll}>
                        Seleccionar Todos
                    </button>
                    <button className="btn btn-primary mb-3" style={{ backgroundColor: '#007bff', borderColor: '#007bff' }} onClick={() => navigate('/home')}>
                        Volver a Home
                    </button>
                </div>
            </div>
        </div>
    );
};

export default MapComponent;
