import axios from "axios";

const USUARIO_BASE_REST_API_URL = "http://localhost:9001/Laboratorio1/personas";

class PersonaService {
    getAll() {
        const token = localStorage.getItem('token'); // Cambia según cómo manejes el token
        return axios.get(USUARIO_BASE_REST_API_URL, {
            headers: {
                'Authorization': `Bearer ${token}`
            }
        }).then(res => {
            return res.data;
        }).catch(error => {
            console.error("Error al obtener las personas:", error);
            throw error;
        });
    }

    register(data) {
        return axios.post(USUARIO_BASE_REST_API_URL, data, {
            headers: {
                'Content-Type': 'application/json'
            }
        }).then(res => {
            return res.data;
        }).catch(error => {
            console.error("Error al registrar:", error);
            throw error;
        });
    }

    authenticate(username, password, apiKey) {
        return axios.post('http://localhost:9001/authenticate', {
            username,
            password
        }, {
            headers: {
                apikey: apiKey
            }
        }).then(res => {
            return res.data; // Devuelve los datos de autenticación
        }).catch(error => {
            console.error("Error al autenticar:", error);
            throw error;
        });
    }
}

// Aquí se exporta una nueva instancia de PersonaService
export default PersonaService();
