/* global google */


import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const MapComponent = () => {
  const [map, setMap] = useState(null);
  const [markers, setMarkers] = useState([]);

  // Ajusta el tamaño del contenedor del mapa
  const containerStyle = {
    width: '100%',
    height: '400px'
  };

  // Establece la posición inicial del mapa
  const center = {
    lat: 4.5709, // Cambia a la latitud deseada
    lng: -74.2973 // Cambia a la longitud deseada
  };

  // Cargar los datos de los marcadores desde la base de datos o API
  useEffect(() => {
    // Simulación de datos de marcadores
    const fetchMarkers = async () => {
      // Reemplaza esto con tu llamada a la API
      const response = await fetch('http://localhost:9001/Laboratorio1/coordenadas'); // Asegúrate de que esta ruta es correcta
      const data = await response.json();
      setMarkers(data);
    };

    fetchMarkers();
  }, []);

  return (
    <LoadScript
      googleMapsApiKey="AIzaSyBomnY9UemevV5IVEXtYHP6SWw3kRsERGc" // Reemplaza con tu API key
      loadingElement={<div>Loading...</div>}
      containerElement={<div style={containerStyle} />}
      mapElement={<div style={{ height: '100%' }} />}
    >
      <GoogleMap
        onLoad={(mapInstance) => setMap(mapInstance)}
        defaultZoom={8}
        defaultCenter={center}
      >
        {markers.map((marker) => (
          <Marker
            key={marker.id} // Cambia 'id' por el identificador único de tu marcador
            position={{ lat: marker.lat, lng: marker.lng }}
            onClick={() => {
              // Acciones al hacer clic en el marcador
              console.log(`Clicked on marker: ${marker.name}`);
            }}
          />
        ))}
      </GoogleMap>
    </LoadScript>
  );
};

export default MapComponent;
