import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { GoogleMap, useLoadScript, MarkerF, InfoWindowF } from '@react-google-maps/api';
import { Link } from 'react-router-dom';
import Select from 'react-select';
import PersonaService from '../services/PersonaService';
import CoordenadasService from '../services/CoordenadasService';

const mapContainerStyle = {
  height: '400px',
  width: '100%',
};

const defaultCenter = { lat: 4.570868, lng: -74.297333 }; // Centro por defecto de Colombia
const libraries = ['places'];

function MapaMultiple() {
  const [personas, setPersonas] = useState([]);
  const [selectedPersonas, setSelectedPersonas] = useState([]);
  const [markers, setMarkers] = useState([]);
  const [activeMarker, setActiveMarker] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: "AIzaSyBomnY9UemevV5IVEXtYHP6SWw3kRsERGc",
    libraries,
  });

  useEffect(() => {
    const fetchPersonas = async () => {
      try {
        const data = await PersonaService.getAllPersons();
        console.log('Datos de personas:', data); // Verifica lo que se estÃ¡ obteniendo
        setPersonas(data);
      } catch (error) {
        console.error('Error fetching personas:', error);
        setError('Error al cargar la lista de personas');
      }
    };

    fetchPersonas();
  }, []);

  useEffect(() => {
    const fetchCoordenadas = async () => {
      setIsLoading(true);
      try {
        if (selectedPersonas.length > 0) {
          const ids = selectedPersonas.map(persona => persona.value);
          const coordenadas = await CoordenadasService.getCoordenadasPorIds(ids);
          const markersData = coordenadas.map(coord => ({
            id: coord.id,
            position: { lat: parseFloat(coord.latitud), lng: parseFloat(coord.longitud) },
            title: coord.marca,
          }));
          setMarkers(markersData);
        } else {
          setMarkers([]);
        }
      } catch (error) {
        console.error('Error fetching coordinates:', error);
        setError('Error al cargar las coordenadas');
      } finally {
        setIsLoading(false);
      }
    };

    fetchCoordenadas();
  }, [selectedPersonas]);

  const handlePersonaChange = (selectedOptions) => {
    setSelectedPersonas(selectedOptions);
  };

  const handleMarkerClick = useCallback((marker) => {
    setActiveMarker(marker);
  }, []);

  const handleInfoWindowClose = useCallback(() => {
    setActiveMarker(null);
  }, []);

  const mapOptions = useMemo(() => ({
    disableDefaultUI: true,
    clickableIcons: false,
    scrollwheel: true,
  }), []);

  const renderMap = useMemo(() => {
    if (!isLoaded) return null;

    return (
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        center={markers.length > 0 ? markers[0].position : defaultCenter}
        zoom={markers.length > 0 ? 10 : 5}
        options={mapOptions}
      >
        {markers.map((marker) => (
          <MarkerF
            key={marker.id}
            position={marker.position}
            onClick={() => handleMarkerClick(marker)}
          >
            {activeMarker === marker && (
              <InfoWindowF
                position={marker.position}
                onCloseClick={handleInfoWindowClose}
              >
                <div>
                  <h3 className="font-bold">{marker.title}</h3>
                  <p>ID: {marker.id}</p>
                  <p>Latitud: {marker.position.lat.toFixed(6)}</p>
                  <p>Longitud: {marker.position.lng.toFixed(6)}</p>
                </div>
              </InfoWindowF>
            )}
          </MarkerF>
        ))}
      </GoogleMap>
    );
  }, [isLoaded, markers, activeMarker, handleMarkerClick, handleInfoWindowClose, mapOptions]);

  if (loadError) {
    return <div>Error al cargar el mapa</div>;
  }

  return (
    <div className="container">
      <div className="row justify-content-center mt-4">
        <div className="col-md-8 bg-white rounded-lg shadow-lg p-4">
          <h1 className="text-2xl font-bold mb-4 text-gray-800">Mapa MÃºltiple</h1>
          
          {personas.length > 0 ? (
            <Select
              isMulti
              options={personas.map(persona => ({
                value: persona.idpersona,
                label: persona.nombre
              }))}
              onChange={handlePersonaChange}
              placeholder="Seleccionar personas"
              className="mb-4"
            />
          ) : (
            <div className="alert alert-info">No hay personas disponibles para seleccionar.</div>
          )}
          
          {error && (
            <div className="alert alert-danger" role="alert">
              <strong>Error!</strong> {error}
            </div>
          )}
          
          {isLoading ? (
            <div className="d-flex justify-content-center align-items-center" style={{ height: '400px' }}>
              <div className="spinner-border" role="status">
                <span className="visually-hidden">Cargando...</span>
              </div>
            </div>
          ) : (
            renderMap
          )}

          <div className="mt-4 text-center">
            <Link to="/home" className="btn btn-primary">
              Volver al Inicio
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
}

export default MapaMultiple;
