import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Table, Button, Modal } from 'react-bootstrap';

const Home = () => {
  const [persons, setPersons] = useState([]);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [personToDelete, setPersonToDelete] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchPersons();
  }, []);

  const fetchPersons = async () => {
    try {
      const token = localStorage.getItem('token');
      const apiKey = localStorage.getItem('apiKey');
      const response = await fetch('http://localhost:9001/Laboratorio1/personas', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'apikey': apiKey || '',
        },
      });
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setPersons(data);
    } catch (error) {
      console.error('Error fetching persons:', error);
    }
  };

  const handleUpdate = (id) => {
    navigate(`/update/${id}`);
  };

  const handleDelete = (person) => {
    setPersonToDelete(person);
    setShowDeleteModal(true);
  };

  const confirmDelete = async () => {
    if (personToDelete) {
      try {
        const token = localStorage.getItem('token');
        const apiKey = localStorage.getItem('apiKey');
        const response = await fetch(`http://localhost:9001/Laboratorio1/persona/${personToDelete.id}`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${token}`,
            'apikey': apiKey || '',
          },
        });
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        setPersons(persons.filter(p => p.id !== personToDelete.id));
        setShowDeleteModal(false);
      } catch (error) {
        console.error('Error deleting person:', error);
      }
    }
  };

  return (
    <div className="container mt-5">
      <h1>Lista de Personas</h1>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre Completo</th>
            <th>Identificación</th>
            <th>Email</th>
            <th>Fecha de Nacimiento</th>
            <th>Edad</th>
            <th>Edad Clínica</th>
            <th>Ubicación</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {persons.map((person) => (
            <tr key={person.id}>
              <td>{person.id}</td>
              <td>{`${person.primerNombre} ${person.segundoNombre} ${person.primerApellido} ${person.segundoApellido}`}</td>
              <td>{person.identificacion}</td>
              <td>{person.email}</td>
              <td>{person.fechaNacimiento}</td>
              <td>{person.edad}</td>
              <td>{person.edadClinica}</td>
              <td>{`${person.ubicacion.lugar}, ${person.ubicacion.ciudad}, ${person.ubicacion.departamento}`}</td>
              <td>
                <Button variant="primary" onClick={() => handleUpdate(person.id)}>Actualizar</Button>
                <Button variant="danger" className="ml-2" onClick={() => handleDelete(person)}>Eliminar</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <Link to="/map">
        <Button variant="info">Ver Ubicaciones en Mapa</Button>
      </Link>

      <Modal show={showDeleteModal} onHide={() => setShowDeleteModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Confirmar Eliminación</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          ¿Está seguro que desea eliminar a {personToDelete?.primerNombre} {personToDelete?.primerApellido}?
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowDeleteModal(false)}>
            Cancelar
          </Button>
          <Button variant="danger" onClick={confirmDelete}>
            Eliminar
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Home;
