import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'primereact/button';
import { InputText } from 'primereact/inputtext';
import 'primereact/resources/themes/saga-blue/theme.css';
import 'primereact/resources/primereact.min.css';
import PersonaService from '../services/PersonaService'; // Asegúrate de importar el servicio correctamente

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [apiKey, setApiKey] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        fetchApiKey(); 
    }, [username]);

    const fetchApiKey = async () => {
        if (username) {
            try {
                const response = await PersonaService.getApiKey(username); // Usa un método del servicio
                setApiKey(response.data.apiKey);
                setError(''); 
            } catch (err) {
                console.error('Error al obtener la API Key:', err);
                setError('No se pudo recuperar la API Key');
            }
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        
        if (!apiKey) {
            setError('No se pudo recuperar la API Key');
            return;
        }

        try {
            const response = await PersonaService.authenticateUser(username, password); // Usa el método del servicio
            console.log(response.data);
            localStorage.setItem('token', response.data.token);
            navigate('/home'); // Navega a Home
        } catch (error) {
            setError('Credenciales inválidas');
            console.error('Error al autenticar:', error);
        }
    };

    return (
        <div className="container-fluid vh-100 d-flex align-items-center justify-content-center" style={{ background: 'linear-gradient(45deg, #74ebd5, #acb6e5)', overflow: 'hidden' }}>
            <div className="card shadow" style={{ maxWidth: '400px', width: '100%', backgroundColor: '#ffffff', borderRadius: '10px' }}>
                <div className="card-header text-center">
                    <h2>Iniciar Sesión</h2>
                </div>
                <div className="card-body">
                    <form onSubmit={handleSubmit}>
                        {error && <div className="alert alert-danger">{error}</div>}
                        <div className="form-group">
                            <label htmlFor="username">Nombre de usuario</label>
                            <InputText
                                type="text"
                                className="form-control mt-2"
                                id="username"
                                placeholder="Ingresa tu nombre de usuario"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                required
                            />
                        </div>
                        <div className="form-group mt-3">
                            <label htmlFor="password">Contraseña</label>
                            <InputText
                                type="password"
                                className="form-control mt-2"
                                id="password"
                                placeholder="Ingresa tu contraseña"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </div>
                        <Button 
                            type="submit" 
                            label="Iniciar Sesión" 
                            className="mt-3" 
                            style={{ width: '100%', backgroundColor: '#007bff', borderColor: '#007bff' }}
                        />
                        <button 
                            type="button" 
                            className="btn btn-link mt-3" 
                            onClick={() => navigate('/register')}
                        >
                            Registrarse
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Login;
