import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Swal from 'sweetalert2';
import PersonaService from '../services/PersonaService';

const Home = () => {
    const [personas, setPersonas] = useState([]); // Inicializa como arreglo vacío
    const [loading, setLoading] = useState(true); // Estado de carga
    const [error, setError] = useState(null); // Estado de error

    useEffect(() => {
        const fetchPersonas = async () => {
            try {
                const response = await PersonaService.getAll();
                console.log("Respuesta de la API:", response);
                setPersonas(response); // Asegúrate de que esta línea esté correctamente configurada según la estructura de respuesta
            } catch (error) {
                console.error("Error al obtener las personas:", error);
                setError(error.message); // Manejo de errores
            } finally {
                setLoading(false); // Fin de la carga
            }
        };

        fetchPersonas();
    }, []);

    const handleDelete = async (id) => {
        const token = localStorage.getItem('token');

        const result = await Swal.fire({
            title: '¿Estás seguro?',
            text: "¡No podrás recuperar este registro!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Sí, eliminarlo!',
        });

        if (result.isConfirmed) {
            try {
                const response = await fetch(`http://localhost:9001/Laboratorio1/personas/${id}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    }
                });

                if (response.ok) {
                    setPersonas(personas.filter(persona => persona.id !== id));
                    Swal.fire('Eliminado!', 'La persona ha sido eliminada.', 'success');
                } else {
                    const errorText = await response.text();
                    console.error("Error al eliminar la persona:", errorText);
                    Swal.fire('Error', errorText || 'No se pudo eliminar el registro.', 'error');
                }
            } catch (error) {
                console.error("Error en la solicitud:", error);
                Swal.fire('Error', 'Error en la solicitud. Por favor, inténtalo de nuevo.', 'error');
            }
        }
    };

    if (loading) return <p>Cargando...</p>; // Mensaje de carga
    if (error) return <p>Error al cargar personas: {error}</p>; // Manejo de errores

    return (
        <div>
            <h1>Lista de Personas</h1>
            <Link to="/register" className="btn btn-primary">Registrar Persona</Link>
            {personas.length === 0 ? (
                <p>No hay personas para mostrar.</p>
            ) : (
                <ul className="list-group mt-3">
                    {personas.map(persona => (
                        <li key={persona.id} className="list-group-item d-flex justify-content-between align-items-center">
                            {persona.nombre} {/* Cambia según el campo que desees mostrar */}
                            <div>
                                <Link to={`/update/${persona.id}`} className="btn btn-warning btn-sm">Editar</Link>
                                <button
                                    onClick={() => handleDelete(persona.id)}
                                    className="btn btn-danger btn-sm ml-2">
                                    Eliminar
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default Home;
