import axios from 'axios';

const API_URL = 'http://localhost:9001/Laboratorio1'; // Cambia esto si es necesario

const AuthService = {
    // Método para iniciar sesión
    login: async (username, password) => {
        const response = await axios.post(`${API_URL}/authenticate`, { username, password });
        return response.data; // Asegúrate de que el servidor devuelva el token en esta respuesta
    },

    // Método para obtener la API Key
    fetchApiKey: async (username) => {
        const token = localStorage.getItem("token"); // Obtener el token
        const response = await axios.get(`${API_URL}/apiKey`, {
            headers: {
                'Authorization': `Bearer ${token}`,
                'username': username,
            },
        });
        return response.data;
    },

    // Método para registrar un nuevo usuario (opcional)
    register: async (username, password) => {
        const response = await axios.post(`${API_URL}/register`, { username, password });
        return response.data;
    }
};

export default AuthService;
