import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const Register = () => {
  const { updateAuthData } = useAuth();
  const [apiKey, setApiKey] = useState('');
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get('http://localhost:9001/Laboratorio1/userdata'); // Asegúrate de que esta ruta sea correcta
        setUserData(response.data);
        setApiKey(response.data.apiKey); // Suponiendo que la apiKey está en la respuesta
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  const handleRegister = async (e) => {
    e.preventDefault();
    if (!userData) {
      alert('No se pudo obtener la información del usuario.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:9001/Laboratorio1/persona', {
        username: userData.username,
        password: userData.password,
        apiKey: apiKey,
      });

      updateAuthData({ username: userData.username, password: userData.password, apiKey });
      alert('Usuario registrado exitosamente!');
    } catch (error) {
      console.error('Error registrando usuario:', error);
      alert('Error al registrar usuario. Verifica la información e inténtalo de nuevo.');
    }
  };

  return (
    <form onSubmit={handleRegister}>
      <h2>Registrar Usuario</h2>
      <button type="submit">Registrar</button>
    </form>
  );
};

export default Register;
