import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Swal from 'sweetalert2';
import 'bootstrap/dist/css/bootstrap.min.css';

const Home = () => {
    const [personas, setPersonas] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        loadPersonas();
    }, []);

    const loadPersonas = async () => {
        try {
            const token = localStorage.getItem('token'); // Obtener el token de localStorage
            const response = await fetch('http://localhost:9001/Laboratorio1/personas', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            const data = await response.json();
            setPersonas(data);
        } catch (error) {
            console.error("Error loading personas:", error);
            setError("Error al cargar las personas.");
        }
    };

    const handleDelete = async (id) => {
        const result = await Swal.fire({
            title: '¿Estás seguro?',
            text: '¡No podrás recuperar este registro!',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: '¡Sí, eliminar!'
        });
    
        if (result.isConfirmed) {
            try {
                const token = localStorage.getItem('token'); // Obtener el token de localStorage
                const response = await fetch(`http://localhost:9001/Laboratorio1/personas/${id}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json', // Asegúrate de incluir el tipo de contenido
                    }
                });
    
                if (response.ok) {
                    console.log("Persona eliminada con éxito.");
                    loadPersonas(); // Recargar la lista después de eliminar
                    Swal.fire('Eliminado', 'El registro ha sido eliminado.', 'success');
                } else {
                    const errorData = await response.json(); // Obtener detalles del error
                    console.error("Error al eliminar la persona:", errorData);
                    Swal.fire('Error', errorData.message || 'No se pudo eliminar el registro.', 'error');
                }
            } catch (error) {
                console.error("Error en la solicitud:", error);
                Swal.fire('Error', 'Ocurrió un error al eliminar el registro.', 'error');
            }
        } else {
            console.log("Eliminación cancelada.");
        }
    };
    
    return (
        <div>
            <h1>Bienvenido a la aplicación</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <table className="table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Primer Nombre</th>
                        <th>Segundo Nombre</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {personas.length > 0 ? (
                        personas.map((persona) => (
                            <tr key={persona.id}>
                                <td>{persona.id}</td>
                                <td>{persona.primerNombre}</td>
                                <td>{persona.segundoNombre}</td>
                                <td>
                                    <Link to={`/update/${persona.id}`} className="btn btn-primary">Actualizar</Link>
                                    <button className="btn btn-danger" onClick={() => handleDelete(persona.id)}>Eliminar</button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="4">No hay personas disponibles.</td>
                        </tr>
                    )}
                </tbody>
            </table>
            <Link to="/map" className="btn btn-secondary">Ver Ubicaciones en Mapa</Link>
        </div>
    );
};

export default Home;
