import React, { useState } from "react";
import SweetAlert from "sweetalert2";

const Register = () => {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    identificacion: "",
    ubicacion: "", // Agrega otros campos según sea necesario
  });

  const [modalMessage, setModalMessage] = useState("");
  const [modalType, setModalType] = useState("");
  const [showModal, setShowModal] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Aquí podrías agregar validaciones previas si es necesario

    try {
      const response = await fetch("http://localhost:9001/Laboratorio1/persona", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData), // Envía formData directamente
      });

      if (response.status === 409) {
        setModalMessage("El usuario ya existe. Por favor, elige otro username o identificacion.");
        setModalType("error");
        setShowModal(true);
        return; // Salir de la función para no procesar más
      }

      let responseData;
      const contentType = response.headers.get("content-type");
      if (contentType && contentType.includes("application/json")) {
        responseData = await response.json();
      } else {
        responseData = await response.text();
      }

      if (response.ok) {
        setModalMessage(typeof responseData === "object" ? responseData.message : responseData);
        setModalType("success");
        setShowModal(true);
      } else {
        setModalMessage(typeof responseData === "object" ? responseData.message : responseData);
        setModalType("error");
        setShowModal(true);
      }
    } catch (error) {
      console.error("Error:", error);
      setModalMessage("Error en la solicitud.");
      setModalType("error");
      setShowModal(true);
    }
  };

  return (
    <div className="container">
      <h2>Registro</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            name="username"
            className="form-control"
            value={formData.username}
            onChange={handleChange}
            required
            autoComplete="username" // Agregado
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            name="password"
            className="form-control"
            value={formData.password}
            onChange={handleChange}
            required
            autoComplete="current-password" // Agregado
          />
        </div>

        <div className="form-group">
          <label htmlFor="identificacion">Identificación:</label>
          <input
            type="text"
            name="identificacion"
            className="form-control"
            value={formData.identificacion}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="ubicacion">Ubicación:</label>
          <input
            type="text"
            name="ubicacion"
            className="form-control"
            value={formData.ubicacion}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit" className="btn btn-primary">Registrar</button>
      </form>

      {showModal && (
        <SweetAlert
          title={modalType === "error" ? "Error" : "Éxito"}
          text={modalMessage}
          type={modalType}
          onConfirm={() => setShowModal(false)}
        />
      )}
    </div>
  );
};

export default Register;
