import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';

const Register = () => {
  const { updateAuthData } = useAuth();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [apiKey, setApiKey] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:9001/Laboratorio1/persona', {
        username,
        password,
        apiKey,
      });

      // Si el registro es exitoso, actualiza los datos de autenticación
      updateAuthData({ username, password, apiKey });
      alert('Usuario registrado exitosamente!');
    } catch (error) {
      console.error('Error registrando usuario:', error);
      alert('Error al registrar usuario. Verifica la información e inténtalo de nuevo.');
    }
  };

  return (
    <form onSubmit={handleRegister}>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        required
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="API Key"
        value={apiKey}
        onChange={(e) => setApiKey(e.target.value)}
        required
      />
      <button type="submit">Registrar</button>
    </form>
  );
};

export default Register;
