import React, { useEffect, useState } from 'react';
import PersonaService from '../services/PersonaService'; // Asegúrate de que la ruta sea correcta

const Home = () => {
    const [personas, setPersonas] = useState([]);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [apiKey, setApiKey] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        // Intenta cargar la lista de personas al montar el componente
        loadPersonas();
    }, []);

    const loadPersonas = async () => {
        try {
            const data = await PersonaService.getAll();
            setPersonas(data);
        } catch (error) {
            console.error("Error loading personas:", error);
        }
    };

    const handleLogin = async () => {
        try {
            const response = await PersonaService.authenticate(username, password, apiKey);
            localStorage.setItem('token', response.token); // Guarda el token en localStorage
            loadPersonas(); // Carga la lista de personas después de autenticar
        } catch (error) {
            setError("Error de autenticación. Verifica tus credenciales.");
        }
    };

    return (
        <div>
            <h1>Bienvenido a la aplicación</h1>
            <div>
                <h2>Iniciar Sesión</h2>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="API Key"
                    value={apiKey}
                    onChange={(e) => setApiKey(e.target.value)}
                />
                <button onClick={handleLogin}>Iniciar Sesión</button>
                {error && <p style={{ color: 'red' }}>{error}</p>}
            </div>

            <h2>Lista de Personas</h2>
            {personas.length > 0 ? (
                <ul>
                    {personas.map((persona) => (
                        <li key={persona.id}>{persona.nombre}</li> // Cambia 'id' y 'nombre' según tu estructura
                    ))}
                </ul>
            ) : (
                <p>No hay personas disponibles.</p>
            )}
        </div>
    );
};

export default Home;
